script:
// This applies globally via CSS injection
if (!document.getElementById('mythril-custom-css')) {
  const style = document.createElement('style');
  style.id = 'mythril-custom-css';
  style.textContent = `
    .layout-side-section, .list-sidebar { display: none !important; }
    .layout-main-section-wrapper { max-width: 100% !important; width: 100% !important; }
    .row.layout-main > .col-lg-10 { flex: 0 0 100% !important; max-width: 100% !important; }
    a[href*="frappe.io/crm"], a[href*="utm_campaign=frappe"] { display: none !important; }
  `;
  document.head.appendChild(style);
}
